import React, { useState } from "react";
import "./App.css";
import Book from "./Components/book"
//import Rover from "./Components/Rover";

function App() {
  //variables
  const [genre, setGenre] = useState("");
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");
  const [coverimg, setCoverimg] = useState("");
  const [pirate_link, setPirate_link] = useState("");
  const [summary, setSummary] = useState("");
  const [visiblity, setVisiblity] = useState(false);

  //api caller
  const fetchDataHandler = () => {
    setVisiblity(true);
    fetch(
      //fetching the api via the link
      "https://dead-tan-beaver-robe.cyclic.app/getbooklist"
    )
      .then((response) => {
        //converting the data into a readable json file
        return response.json();
      })
      .then((data) => {
        //getting a random number
        var min = 1;
        var max = data.length;
        var rand = Math.trunc(min + Math.random() * (max - min));
        console.log(rand);

        console.log(data);
        //choosing the book
        var bookobj = data[rand];
        setGenre(bookobj.genre);
        setName(bookobj.name);
        setAuthor(bookobj.author);
        setCoverimg(bookobj.coverimg);
        var coverimG = bookobj.coverimg
        console.log(coverimG)
        setPirate_link(bookobj.pirate_link);
        setSummary(bookobj.summary);
      });
  };

  const revert = () => {
    setVisiblity(false);
  };

  return (
    <div className="App">
      {visiblity === false ? (
        <div>
          <section>
            Hello, I am a book reccomendation engine called Bookinator
          </section>
          <button onClick={fetchDataHandler}>Render</button>
        </div>
      ) : (
        <div>
          <Book coverimg={coverimg} name={name} summary={summary} author={author} genre={genre} pirate_link={pirate_link}/>
          <button onClick={revert}>Return</button>
        </div>
      )}
    </div>
  );
}

export default App;
