import React from "react";
import { useState,useEffect,useRef } from "react";
import user from "../src/user.png"
function Drop()
{
  const [open,setOpen]=useState(false);
  let menuRef=useRef();
  useEffect(()=>{
    let handler = (e)=>{
      if(!menuRef.current.contains(e.target)){
        setOpen(false);
        console.log(menuRef.current);
      }
    };
    document.addEventListener("mousedwon",handler);
    return()=>{
      document.removeEventListener("mousedown",handler);
    }
  });
  return(
    <div className="app">
      <div className=" menu-container" ref={menuRef}>
      <div className="menu-trigger" onClick={()=>{setOpen(!open)}}>
      </div>
      <div className={`dropdown-menu ${open?'active':'inactive'}`}>
      <h3>bookinators<br/><span>9000</span></h3>
      <ul>
        <DropdownItem text={"Romance"}/>
        <DropdownItem text={"Fantasy"}/>
        <DropdownItem text={"Fiction"}/>
      </ul>
      </div>
      </div>
      </div>



  )
}
function DropdownItem(props){
  return(
    <li className="dropdownItem">
      <img src={props.img}></img>
      <a>{props.text}</a>

    </li>
  )
}

export default function Navbar() {
  return (

    <div className="Navbar">
      <table class="reachmeout">
        <tr>

          <td>
            <a
              href="https://www.linkedin.com/in/harsh-pandit-4b1013235/"
              class="stylisethisbutton"
              title="LinkedIn"
            >
              <i class="fab fa-linkedin"></i>
            </a>
          </td>
          <td>
            <a
              href="https://github.com/harshpandit2004/bookinator-frontend"
              class="stylisethisbutton"
              title="Github"
            >
              <i class="fab fa-github"></i>
            </a>
          </td>
        </tr>
      </table>
    </div>
  );
}
