import React, { useState } from "react";
import "./App.css";
//import Rover from "./Components/Rover";
import APOD from "./Components/APOD";

function App() {
  //variables
  const [genre, setGenre] = useState("");
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");
  const [coverimg, setCoverimg] = useState("");
  const [pirate_link, setPirate_link] = useState("");
  const [summary, setSummary] = useState("");
  const [visiblity, setVisiblity] = useState(false);

  //api caller
  const fetchDataHandler = () => {
    setVisiblity(true);
    fetch(
      //fetching the api via the link
      "https://dead-tan-beaver-robe.cyclic.app/getbooklist"
    )
      .then((response) => {
        //converting the data into a readable json file
        return response.json();
      })
      .then((data) => {
        //getting a random number
        var min = 1;
        var max = data.length;
        var rand = Math.trunc(min + Math.random() * (max - min));
        console.log(rand);

      });
  };

  const revert = () => {
    setVisiblity(false);
  };

  return (
    <div className="App">
      {visiblity === false ? (
        <div>
          <section>
            Hello, I can get you,
            <a href="https://en.wikipedia.org/wiki/Astronomy_Picture_of_the_Day">
              NASA's Astronomy Picture of The Day
            </a>
          </section>
          <button onClick={fetchDataHandler}>Render</button>
        </div>
      ) : (
        <div>
          <button onClick={revert}>Return</button>
        </div>
      )}
    </div>
  );
}

export default App;
